import React, { useState, useEffect , useCallback } from "react";
import MetaTags from "react-meta-tags";  
import BootstrapTable from "react-bootstrap-table-next"
import paginationFactory from "react-bootstrap-table2-paginator";
import axios from 'axios';  
//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import '../../config';
import {
  Row,
  Col,
  Card,
  CardBody,
  CardTitle,
  Container,
  UncontrolledAlert,
  Button
} from "reactstrap";

import { getData as onGetData } from "../../store/donwloaddata/actions"

//redux
import { useSelector, useDispatch } from "react-redux"

const columnFormatter = (cell, row, rowIndex, formatExtraData) => {
  if (row && row.fileNm) {
    return (
      <a
        href="#"
        onClick={() => {
          console.log(row.fileLoc+row.fileNm);
            var indexed_array = {
              "file_name" : row.fileNm,
              "file_location" : row.fileLoc
            };
            donwloadData(indexed_array);
            
        }}
      >
        {row.fileNm}
      </a>
    );
  } else {
    return row.fileNm;
  }
};

const columnFormatterOriginal = (cell, row, rowIndex, formatExtraData) => {
  if (row && row.fileNmOld) {
    return (
      <a
        href="#"
        onClick={() => {
          var indexed_array = {
            "file_name" : row.fileNmOld,
            "file_location" : row.fileLoc
          };
          donwloadData(indexed_array)
    
        }}
      >
        {row.fileNmOld}
      </a>
    );
  } else {
    return row.fileNmOld;
  }
};

function donwloadData(indexed_array){
    
  axios({
    url: "http://localhost:9002/app/"+"app001/get-file-image?" + $.param(indexed_array), 
    method: 'GET',
    headers : {token : localStorage.getItem('authUser').replace(/^"(.*)"$/, '$1')},
    responseType: 'blob', // important
  }).then((response) => {
    console.log(response.data);
    if(response.data.size != 0){
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', indexed_array.file_name);
      document.body.appendChild(link);
      link.click();
    }else{
      alert("Data Not Found");
    }
  })
  .catch((error) => {
    if(error.response.status === 400){
      alert("No access to donwload file");
    }else{
      alert("Data Not Found");
    }
   
  });;
}


const DonwloadData = () => {
  
  const columns = [
    {
      dataField: "id",
      text: "ID",
      sort: true
    },
    {
      dataField: "poc",
      text: "POC",
      sort: true
    },
    {
      dataField: "userId",
      text: "User ID",
      sort: true
    },
    {
      dataField: "userNm",
      text: "User Name",
      sort: true
    },
    {
      dataField: "dateipt",
      text: "Tanggal Entry",
      sort: true
    },
    {
      dataField: "fileNm",
      text: "File Edit",
      sort: true,
      formatter: columnFormatter  
    },
    {
      dataField: "fileNmOld",
      text: "File Original",
      sort: true,
      formatter: columnFormatterOriginal  
    },
  ]
  
  const dispatch = useDispatch()
  const [dataEntry, setdataEntry] =  useState([]);
  const entrydata  = useSelector(state => {
    return state.GetData.resp.data;
  });

  const error  = useSelector(state => {
    return state.GetData.error;
  });

  useEffect(() => {
    setdataEntry([])
    dispatch(onGetData())
    setdataEntry(entrydata);
  }, [dispatch])

  // const [isSending, setIsSending] = useState(false)
  // const sendRequest = useCallback(async () => {
  //   console.log('KLIK');
  //   dispatch(onGetData())
   
  // }, [isSending]) // update the callback if the state changes

  return (
    <React.Fragment>
      <div className="page-content">
        <MetaTags>
          <title>
          Korindo App
          </title>
        </MetaTags>
        <Container fluid={true}>
          <Breadcrumbs title="Forms" breadcrumbItem="Download foto dengan marking" />
         
          {error ? <UncontrolledAlert color="danger" role="alert">{error}</UncontrolledAlert> : null} 
                    {/* <Button disabled={isSending} onClick={sendRequest}
                      color="primary"
                      className="btn btn-primary col-sm-1"
                    >
                      Refresh
                    </Button> */}
          <Row>
              
                    
            <Col>
              <Card>
                <CardBody>
                  <CardTitle>List Entry Data Marking </CardTitle>

                  <div className="table-responsive">
                    <BootstrapTable
                      keyField="id"
                      data={dataEntry}
                      columns={columns}
                      striped
                      hover
                      condensed
                      pagination={paginationFactory()}
                    />
                  </div>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>
  );
};
  
export default DonwloadData
