import React, { useState, useEffect } from "react";
import MetaTags from "react-meta-tags";  
import BootstrapTable from "react-bootstrap-table-next"
import paginationFactory from "react-bootstrap-table2-paginator";
import axios from 'axios';  
//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import {
  Row,
  Col,
  Card,
  CardBody,
  FormGroup,
  Button,
  CardTitle,
  CardSubtitle,
  Label,
  Input,
  Container,
  FormFeedback,
  Form
} from "reactstrap";


const columnFormatter = (cell, row, rowIndex, formatExtraData) => {
  if (row && row.fileNm) {
    return (
      <a
        href="#"
        onClick={() => {
          console.log(row.fileLoc+row.fileNm);
          axios.get('http://localhost:9002/app/app001/get-file-image', {
            params: {
              file_name: row.fileNm,
              file_location : row.fileLoc,
            }
          }).then((response) => {
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', row.fileNm);
            document.body.appendChild(link);
            link.click();
          });
        }}
      >
        {row.fileNm}
      </a>
    );
  } else {
    return row.fileNm;
  }
};

const columnFormatterOriginal = (cell, row, rowIndex, formatExtraData) => {
  if (row && row.fileNmOld) {
    return (
      <a
        href="#"
        onClick={() => {
          var indexed_array = {
            "file_name" : row.fileNmOld,
            "file_location" : row.fileLoc
          };
          var url = "http://localhost:9002/app/app001/get-file-image?" + $.param(indexed_array);
          // console.log(row.fileLoc+row.fileNmOld);
          // axios.get('http://localhost:9002/app/app001/get-file-image', {
          //   params: {
          //     file_name: row.fileNmOld,
          //     file_location : row.fileLoc,
          //   }
          // }).then((response) => {
            var win = window.open(url, '_blank');
            win.focus();
          //});
        }}
      >
        {row.fileNmOld}
      </a>
    );
  } else {
    return row.fileNmOld;
  }
};


const DonwloadData = () => {
  const columns = [
    {
      dataField: "id",
      text: "ID",
      sort: true
    },
    {
      dataField: "poc",
      text: "POC",
      sort: true
    },
    {
      dataField: "userId",
      text: "User ID",
      sort: true
    },
    {
      dataField: "userNm",
      text: "User Name",
      sort: true
    },
    {
      dataField: "dateipt",
      text: "Tanggal Entry",
      sort: true
    },
    {
      dataField: "fileNm",
      text: "File Edit",
      sort: true,
      formatter: columnFormatter  
    },
    {
      dataField: "fileNmOld",
      text: "File Original",
      sort: true,
      formatter: columnFormatterOriginal  
    },
  ]
  const [dataEntry, setdataEntry] =  useState([]);
  useEffect(() => {
    axios({
      method: "post",
      url: "http://localhost:9002/app/app001/get-all",
      data: {},
      headers: { "Content-Type": "application/json" },
    })
      .then(function (response) {
        //handle success
        if(response.data.message = 1){
          setdataEntry(response.data.data);
        }
        
      })
      .catch(function (response) {
        //handle error
        console.log(response);
      });
  }, [])
  return (
    <React.Fragment>
      <div className="page-content">
        <MetaTags>
          <title>
            Form Validation | Skote - React Admin & Dashboard Template
          </title>
        </MetaTags>
        <Container fluid={true}>
          <Breadcrumbs title="Forms" breadcrumbItem="Download foto dengan marking" />
          <Row>
            <Col>
              <Card>
                <CardBody>
                  <CardTitle>Datatable Editable </CardTitle>

                  <div className="table-responsive">
                    <BootstrapTable
                      keyField="id"
                      data={dataEntry}
                      columns={columns}
                      striped
                      hover
                      condensed
                      pagination={paginationFactory()}
                    />
                  </div>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>
  );
};
  
export default DonwloadData
