import axios from "axios"
import accessToken from "./jwt-token-access/accessToken"

//pass new generated access token here
const token = accessToken

//apply base url for axios
const API_URL = "http://localhost:9001/opims"

const axiosApi = axios.create({
  baseURL: API_URL,
})

axiosApi.defaults.headers.common["Authorization"] = token
axiosApi.defaults.headers.post['Content-Type'] ='application/json;charset=utf-8';
axiosApi.defaults.headers.post['Access-Control-Allow-Origin'] = '*';

axiosApi.interceptors.response.use(
  response => response,
  error => Promise.reject(error)
)

export async function get(url, config = {}) {
  return await axiosApi.get(url, { ...config }).then(response => response.data)
}

export async function post(url, data, config = {}) {
  console.log("AXIOS URL : "+API_URL+url);
  console.log("AXIOS DATA : "+JSON.stringify(data));
  console.log("AXIOS CONFIG : "+JSON.stringify(axiosApi));
  return axiosApi
    .post("localhost:9001/opims/opm001/login", { ...data }, { ...config })
    .then(response => response.data)
    .catch(
      function (error) {
        console.log('Show error notification!'+JSON.stringify(error))
        return Promise.reject(error)
      }
    )
}


export async function put(url, data, config = {}) {
  return axiosApi
    .put(url, { ...data }, { ...config })
    .then(response => response.data)
}

export async function del(url, config = {}) {
  return await axiosApi
    .delete(url, { ...config })
    .then(response => response.data)
}
