import {
    GET_DATA,
    GET_DATA_SUCCESS,
    GET_DATA_ERROR
  } from "./actionTypes"

  const INIT_STATE = {
    resp: [{data: []}],
    error: "",
    loading: false,
  }

  const getData = (state = INIT_STATE, action) => {
    
    switch (action.type) {
      case GET_DATA:
        return {
          ...state,
          loading: true,
        }
  
      case GET_DATA_SUCCESS:
        return {
          ...state,
          resp: action.payload,
          loading: false,
        }
  
      case GET_DATA_ERROR:
        return {
          ...state,
          error: action.payload,
          loading: false,
        }
  
      default:
        return state
    }
  }
  
  export default getData
