import React, { useState, useEffect , useCallback } from "react";
import MetaTags from "react-meta-tags";  
import BootstrapTable from "react-bootstrap-table-next"
import paginationFactory from "react-bootstrap-table2-paginator";
//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import '../../config';
import {
  Row,
  Col,
  Card,
  CardBody,
  CardTitle,
  Container,
  UncontrolledAlert,
  Button,
  Alert
} from "reactstrap";

import { getData as onGetData, getDataImage as onGetDataImage } from "../../store/donwloaddata/actions"

//redux
import { useSelector, useDispatch } from "react-redux"

const DonwloadData = () => {

  const dispatch = useDispatch()

  function getImage(indexed_array) {
    dispatch(onGetDataImage(indexed_array));
  }

  const entrydata  = useSelector(state => {
    return state.GetData.resp.data;
  });

  const error  = useSelector(state => {
    return state.GetData.error;
  });

  useEffect(() => {
    console.log(error);
    error = null;
    dispatch(onGetData())
  }, [dispatch])

  const columnFormatter = (cell, row, rowIndex, formatExtraData) => {
    if (row && row.fileNm) {
      return (
        <a
          href="#"
          onClick={() => {
              var indexed_array = {
                "file_name" : row.fileNm,
                "file_location" : row.fileLoc
              };
              getImage(indexed_array);
              
          }}
        >
          {row.fileNm}
        </a>
      );
    } else {
      return row.fileNm;
    }
  };

  const columnFormatterOriginal = (cell, row, rowIndex, formatExtraData) => {
    if (row && row.fileNmOld) {
      return (
        <a
          href="#"
          onClick={() => {
            var indexed_array = {
              "file_name" : row.fileNmOld,
              "file_location" : row.fileLoc
            };
            getImage(indexed_array)
      
          }}
        >
          {row.fileNmOld}
        </a>
      );
    } else {
      return row.fileNmOld;
    }
  };
  
  const columns = [
    {
      dataField: "id",
      text: "ID",
      sort: true
    },
    {
      dataField: "poc",
      text: "POC",
      sort: true
    },
    {
      dataField: "userId",
      text: "User ID",
      sort: true
    },
    {
      dataField: "userNm",
      text: "User Name",
      sort: true
    },
    {
      dataField: "dateipt",
      text: "Tanggal Entry",
      sort: true
    },
    {
      dataField: "fileNm",
      text: "File Edit",
      sort: true,
      formatter: columnFormatter  
    },
    {
      dataField: "fileNmOld",
      text: "File Original",
      sort: true,
      formatter: columnFormatterOriginal  
    },
  ]

  const [isSending, setIsSending] = useState(false)
  const sendRequest = useCallback(async () => {
    dispatch(onGetData());
  }, [entrydata]) // update the callback if the state changes

  return (
    <React.Fragment>
      <div className="page-content">
        <MetaTags>
          <title>
          Korindo App
          </title>
        </MetaTags>
        <Container fluid={true}>
          <Breadcrumbs title="Forms" breadcrumbItem="Download foto dengan marking" />

          {error ?<Alert color="danger" role="alert">{error}</Alert>: null} 
         
                   <Button disabled={isSending} onClick={sendRequest}
                      color="primary"
                      className="btn btn-primary col-sm-1"
                    >
                      Refresh
                    </Button>
          <Row>
              
                    
            <Col>
              <Card>
                <CardBody>
                  <CardTitle>List Entry Data Marking </CardTitle>

                  <div className="table-responsive">
                    <BootstrapTable
                      keyField="id"
                      data={entrydata}
                      columns={columns}
                      striped
                      hover
                      condensed
                      pagination={paginationFactory()}
                    />
                  </div>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>
  );
};
  
export default DonwloadData
